// page init
jQuery(function(){
	passwordShow();
	initSwitchSelect();
	initSelectAllCheckbox();
	initTabs();
	jcf.customForms.replaceAll();
	initDropDown();
	initLightbox();
	jQuery('input, textarea').placeholder();
});

function passwordShow(){
	$('#password-1').hidePassword(true);
}
// switch select init
function initSwitchSelect(){
	jQuery('.switch-select').each(function(){
		var select = jQuery(this);
		var options = select.find('option');
		
		function switchHandler(){
			var currOption = options.eq(select.get(0).selectedIndex);
			var href = currOption.data('url');
			jQuery.fancybox({
				href: href,
				padding: 0,
				margin: 0,
				cyclic: false,
				autoScale: true,
				overlayShow: true,
				overlayOpacity: 0.65,
				overlayColor: '#000000',
				titlePosition: 'inside'
			});
		}
		
		select.bind('change', switchHandler);
	});
}

// select all checkbox
function initSelectAllCheckbox(){
	jQuery('.checkbox-holder').each(function(){
		var holder = jQuery(this);
		var selectAllCheckbox = holder.find('.checkbox-heading input[type="checkbox"]');
		var checkboxes = holder.find('.checkbox-area input[type="checkbox"]');
		
		function selectedHandler(){
			checkboxes.each(function(){
				var checkbox = jQuery(this);
				
				if(selectAllCheckbox.is(':checked')) {
					checkbox.attr('checked', 'checked');
				} else {
					checkbox.removeAttr('checked');
				}
				
				if(checkbox.get(0).jcf) {
					checkbox.get(0).jcf.refreshState();
				}
			});
		}
		
		checkboxes.each(function(){
			var checkbox = jQuery(this);
			
			checkbox.bind('change', function(){
				if(selectAllCheckbox.is(':checked')) {
					selectAllCheckbox.removeAttr('checked');
					if(selectAllCheckbox.get(0).jcf) {
						selectAllCheckbox.get(0).jcf.refreshState();
					}
				}
			});
		});
		
		selectedHandler();
		selectAllCheckbox.bind('change', selectedHandler);
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').contentTabs({
		addToParent: true,
		tabLinks: 'a'
	});
}

// animated navigation init
function initDropDown() {
	jQuery('.setting-holder').animDropdown({
		items: '.holder',
		drop: '.setting-area',
		animSpeed: 200,
		effect: 'fade'
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').each(function(){
		var link = jQuery(this);
		link.fancybox({
			padding: 0,
			margin: 0,
			cyclic: false,
			autoScale: true,
			overlayShow: true,
			overlayOpacity: 0.65,
			overlayColor: '#000000',
			titlePosition: 'inside',
			onComplete: function(box) {
				if(link.attr('href').indexOf('#') === 0) {
					jQuery('#fancybox-content').find('a.close').unbind('click.fb').bind('click.fb', function(e){
						jQuery.fancybox.close();
						e.preventDefault();
					});
				}
			}
		});
	});
}

